mapper.delete:
delete from review_tb
where
    ACADEMY_ID = #{academyId}
    and user_id = #{userId};

mapper.select:
select count(*) FROM academy_tb WHERE 1 = 1
AND ATPT_OFCDC_SC_CODE = #{atptOfcdcScCode}
AND ADMST_ZONE_NM = #{admstZoneNm}
AND ACA_ASNUM = #{acaAsnum}
AND ACA_NM LIKE CONCAT('%', #{acaNm}, '%')
AND REALM_SC_NM = #{realmScNm}
AND LE_ORD_NM = #{leOrdNm}
AND LE_CRSE_NM = #{leCrseNm}
AND ACADEMY_ID IN ( SELECT ait.ACADEMY_ID FROM academy_tb at LEFT OUTER JOIN academy_info_tb ait ON ait.ACADEMY_ID = at.ACADEMY_ID LEFT OUTER JOIN attendance_age_tb aat ON aat.academy_info_id = ait.academy_info_id WHERE aat.age_id IN
#{ageId}
GROUP BY ait.ACADEMY_ID HAVING COUNT(DISTINCT aat.age_id) = #{countAgeId} )
AND ACADEMY_ID IN ( SELECT ait.ACADEMY_ID FROM academy_tb at LEFT OUTER JOIN academy_info_tb ait ON ait.ACADEMY_ID = at.ACADEMY_ID LEFT OUTER JOIN academy_convenience_tb act ON act.academy_info_id = ait.academy_info_id WHERE act.convenience_id IN
#{convenienceId}
GROUP BY ait.ACADEMY_ID HAVING COUNT(DISTINCT act.convenience_id) = #{countConvenienceId} )

SELECT at.ACADEMY_ID, at.ATPT_OFCDC_SC_CODE, at.ATPT_OFCDC_SC_NM, at.ADMST_ZONE_NM, at.ACA_INSTI_SC_NM, at.ACA_ASNUM, at.ACA_NM, at.ESTBL_YMD, at.REG_YMD, at.REG_STTUS_NM, at.CAA_BEGIN_YMD, at.CAA_END_YMD, at.TOFOR_SMTOT, at.DTM_RCPTN_ABLTY_NMPR_SMTOT, at.REALM_SC_NM, at.LE_ORD_NM, at.LE_CRSE_LIST_NM, at.LE_CRSE_NM, at.PSNBY_THCC_CNTNT, at.THCC_OTHBC_YN, at.BRHS_ACA_YN, at.FA_RDNMA, at.FA_RDNDA, at.FA_RDNZC, at.FA_TELNO, at.LOAD_DTM, ait.logo_img FROM academy_tb at left outer join academy_info_tb ait on(ait.ACADEMY_ID = at.ACADEMY_ID) WHERE 1 = 1
AND at.ATPT_OFCDC_SC_CODE = #{atptOfcdcScCode}
AND at.ADMST_ZONE_NM = #{admstZoneNm}
AND at.ACA_ASNUM = #{acaAsnum}
AND at.ACA_NM LIKE CONCAT('%', #{acaNm}, '%')
AND at.REALM_SC_NM = #{realmScNm}
AND at.LE_ORD_NM = #{leOrdNm}
AND at.LE_CRSE_NM = #{leCrseNm}
AND at.ACADEMY_ID IN ( SELECT ait.ACADEMY_ID FROM academy_tb at LEFT OUTER JOIN academy_info_tb ait ON ait.ACADEMY_ID = at.ACADEMY_ID LEFT OUTER JOIN attendance_age_tb aat ON aat.academy_info_id = ait.academy_info_id WHERE aat.age_id IN
#{ageId}
GROUP BY ait.ACADEMY_ID HAVING COUNT(DISTINCT aat.age_id) = #{countAgeId} )
AND at.ACADEMY_ID IN ( SELECT ait.ACADEMY_ID FROM academy_tb at LEFT OUTER JOIN academy_info_tb ait ON ait.ACADEMY_ID = at.ACADEMY_ID LEFT OUTER JOIN academy_convenience_tb act ON act.academy_info_id = ait.academy_info_id WHERE act.convenience_id IN
#{convenienceId}
GROUP BY ait.ACADEMY_ID HAVING COUNT(DISTINCT act.convenience_id) = #{countConvenienceId} )
LIMIT #{index}, #{pSize}
